// FileName:  PrintFDemos.javapackage ch05;public class PrintFDemos{	public static void main(String [] args)	{			int fahrenheit = 212;				System.out.printf("%7d%n", fahrenheit);		System.out.printf("Temperature = %7d%n", fahrenheit);		System.out.printf("Temperature = %7d Fahrenheit%n", fahrenheit);		System.out.printf ("%-7d100%n", fahrenheit);		System.out.println();	System.out.println();					// **********************		double percentage = 99.3456789;				System.out.printf ("%10.4f%n", percentage);		System.out.printf ("%-10.4f%n", percentage);				System.out.println();	System.out.println();					// **********************		String phrase = "Java is Cool!";				System.out.printf ("%20s%n", phrase);		System.out.printf ("%-20s%n", phrase);		System.out.printf("%7d%10.4f%20s%n", fahrenheit, percentage, phrase);		System.out.println();	System.out.println();					// **********************		int x = (int) (Math.random() * 11 ) - 5;		System.out.println("The random integer is " + x);		System.out.println("Here are the square roots of integers below " + x + "\n");		while (x > 0) 		{			double root = Math.sqrt(x);			//System.out.println("The square root of x is " + root);			System.out.printf("The square root of%3d is%8.4f%n", x, root);			//System.out.printf("The square root of%3d is%8.4f\n", x, root);			x--;		}		System.out.println();		System.out.println();				// *******************************************************************		System.out.println("The following code prints whether each integer ");		System.out.println("between 1 and 10 is even or odd.\n");		int cntr = 1; 		while (cntr <= 10)		{			if (cntr % 2 == 0)			{				System.out.printf("%3d is even%n", cntr);				//System.out.println(cntr + " is even");			}			else			{				System.out.printf("%3d is odd%n", cntr);				//System.out.println(cntr + " is odd");			}			cntr++;		}		System.out.println();		System.out.println();		// *******************************************************************		// Write a while loop that will print 10 random integers in the range		// from 25 to 75 inclusive.  The loop should print each random		// integer as it is generated inside the loop.		System.out.println("Random integers between 25 and 75 inclusive:\n"); 		cntr = 1;		while (cntr <= 10)		{			x = (int) (Math.random() * 51) + 25;			System.out.printf("The random integer is %3d%n", x); 			//System.out.println("The random integer is " + x); 			cntr++;		}		System.out.println();		System.out.println("Program Terminated");	} // end of main method} // end of PrintFDemos class