// FileName:  WhileLoopMania1.javapackage ch05;import java.util.Scanner;public class WhileLoopMania1{	public static void main(String [] args)	{			// Part 1.  Write the code to sum the integers from 26 to 50 inclusive.		// Write a while loop that counts forwards to do this, then print the		// sum after the loop is over.		int sum = 0;		int cntr = 26;		while (cntr <=50)		{			sum += cntr;			cntr ++;		}		System.out.println("The sum of number 26 to 50 is: " + sum);		// *******************************************************************		System.out.println();		System.out.println();		// Part 2.  Reverse sum the integers from 26 to 50 inclusive.		// Write a while loop that counts backwards to do this.		// It should calculate the sum by first adding 50 to 49 to 48, etc.,		// then print the sum after the loop is over.		sum = 0;		cntr = 50;		while (cntr >= 26)		{			sum -= cntr;			cntr ++;		}		// *******************************************************************		System.out.println();		System.out.println();		// Part 3.  Write a code segment that uses a while loop that runs ten		// times.  During each iteration of the loop, print out the random		// value of each of a pair of dice.  You should have two die variables		// and generate a random integer for each die during each iteration of		// the loop.  The random value for each die should be inclusive for the		// values we would expect a die to have.  The output should look like:		/*					Roll #1					Die 1 is a 5					Die 2 is a 1					Roll #2					Die 1 is a 4					Die 2 is a 2					etc.		 */		cntr = 1;		while (cntr <= 10)		{			int die1 = (int)(Math.random()*6)+1;			int die2 = (int)(Math.random()*6)+1;			System.out.println("Roll #" +cntr);			System.out.println("Die 1 is a " + die1);			System.out.println("Die 2 is a " + die2);			System.out.println();			cntr++;		}		// *******************************************************************		System.out.println();		System.out.println();		/* 		Part 4:  Write a while loop that will print the		square roots of the first 10 multiples of 3.  The		output should be:		The square root of 3 is 1.7320508075688772		The square root of 6 is 2.449489742783178		The square root of 9 is 3.0		The square root of 12 is 3.4641016151377544		The square root of 15 is 3.872983346207417		The square root of 18 is 4.242640687119285		The square root of 21 is 4.58257569495584		The square root of 24 is 4.898979485566356		The square root of 27 is 5.196152422706632		The square root of 30 is 5.477225575051661		 */		cntr = 3;		while (cntr <= 30)		{			double root = Math.sqrt(cntr);			System.out.println("The square root of " + cntr + " is " + root);			cntr = cntr + 3;		}		// *******************************************************************		System.out.println();		System.out.println();		// Part 5.  Remainder-itis.		// startingValue and endingValue will be entered from the keyboard, and		// we won't know what values are being entered.  Your job is to write		// a while loop to display the remainder of dividing each number between		// startingValue and endingValue by 5 with a statement that would make the		// output appear as:  "The remainder of the division is ?"		// Here is sample output if the values entered from the keyboard were		// 10 for starting value and 20 for ending value:		//		The remainder of the division is 0		//		The remainder of the division is 1		//		The remainder of the division is 2		//		The remainder of the division is 3		//		The remainder of the division is 4		//		The remainder of the division is 0		//		The remainder of the division is 1		//		The remainder of the division is 2		//		The remainder of the division is 3		//		The remainder of the division is 4		//		The remainder of the division is 0		Scanner reader = new Scanner(System.in);		System.out.print("Enter the starting value: ");		int startingValue = reader.nextInt(); 		System.out.print("Enter the ending value: ");		int endingValue  = reader.nextInt();		int remainder;		if (startingValue <= endingValue)		{			while (startingValue <= endingValue)			{				remainder = startingValue%5;				System.out.println("The remainder of the division is " + remainder);				startingValue ++;			}		}		else if (endingValue <= startingValue)		{			while (endingValue <= startingValue)			{				remainder = endingValue%5;				System.out.println("The remainder of the division is " + remainder);				endingValue++;			}		} 		//ASK CONREY IF WE SHOULD ACCOUNT FOR DOUBLES		// *******************************************************************		System.out.println();		System.out.println();		// Part 6.  Stop and write the method calculateFactorial after the instructions 		// found below the main method, then continue with the instructions below.		// Write a while true loop that will do these things inside the loop:		//		// 1) Prompt the user to "Enter an integer greater than 0 or -1 to quit".		//	  Read the integer from the keyboard and store it in the variable number.		//		// 2) Check the value entered immediately to see if -1 was entered.  If it is,		//    then quit the loop immediately without doing anything else.		//		// 3) Check the value to see if a negative integer other than -1 was entered.		//    If it is, then output "The number you enter must be greater than 0!"		//	  Otherwise, call the calculateFactorial method passing it the number 		//	  entered from the keyboard, catch the value returned with some variable,		//    and then print out the factorial value stored in that variable.		// Note:  Scanner reader is already declared.		System.out.println("Let's calculate some factorials!");						System.out.println("Enter an integer greater than 0, or enter -1 to quit: ");		int number = reader.nextInt();				int factorial = 1;						while(true)		{			if (number == -1)				break;			else if (number < -1)				System.out.println("The number you enter must be greater than 0!");			else if (number >0)			{				factorial = calculateFactorial(number);				break;			}		}		System.out.println("The factorial of " + number + " is " + factorial );		System.out.println();		System.out.println("Program Terminated.");	} // end of main method	// Write the method calculateFactorial that receives one parameter	// of type int named num.  The method will calculate and return the	// factorial of num.  THIS METHOD DOES NOT PRINT ANYTHING!		public static int calculateFactorial(int num)		{		int factorial = 1;				while(num > 0)		{			factorial *= num;			num--;		}		return factorial;	}} // end of WhileLoopMania1 class