package ch08;// FileName:  Pet.java// Author: Izzy Geller  // Write the class declaration line for the class Petpublic class Pet{	// Declare the instance variables for a Pet object.	// Each Pet object has a name, type, age, and weight.	// The name and type instance variables are type String and 	// age is an integer and weight is a floating-point.	private String name;	private String type;	private int age;	private double weight;		// Write the Default Constructor and set the instance variables	// to appropriate default values.	public Pet()	{		name = "";		type ="";		age = 0;		weight = 0.0;	}		// Write the Initializing Constructor and set the instance variables	// to the parameter values passed to this constructor.	public Pet (String nm, String typ, int ag, double wt)	{		name = nm;		type = typ;		age = ag;		weight = wt;	}		// Write the Copy Constructor and set the instance variables	// of the Pet being constructed to the values of the instance	// variables of the Pet parameter p.	public Pet (Pet p)	{		name = p.name;		type = p.type;		age = p.age;		weight = p.weight;	}	// ACCESSOR METHODS	// Write the accessor method that will return the value of	// the instance variable name.  The method's name is getName.	public String getName()	{		return name;	}	// Write the accessor method that will return the value of	// the instance variable type.  The method's name is getType.	public String getType()	{		return type;	}	// Write the accessor method that will return the value of	// the instance variable age.  The method's name is getAge.	public int getAge ()	{		return age;	}	// Write the accessor method that will return the value of	// the instance variable weight.  The method's name is getWeight.	public double getWeight()	{		return weight;	}			// MUTATOR OR MODIFIER METHODS	// Write the mutator method that will set the instance variable name	// to the value of the String parameter.  The method's name is setName.	public void setName (String nm)	{		name = nm;	}	// Write the mutator method that will set the instance variable type	// to the value of the String parameter.  The method's name is setType.	public void setType (String typ)	{		type = typ;	}	// Write the mutator method that will set the instance variable age	// to the value of the integer parameter.  The method's name is setAge.	public void setAge (int ag)	{		age = ag;	}	// Write the mutator method that will set the instance variable weight	// to the value of the double parameter.  The method's name is setWeight.	public void setWeight (double wt)	{		weight = wt;	}			// TOSTRING METHOD	// Construct and return a string representation of a Pet object	// outputting the values of the private data members along with	// an identification of what each is. Use concatenation and "\n" characters.	public String toString()	{		String str;		str = 	"\nName: 		" + name + "\n" + 				"Type:  " + type + "\n" +				"Age:  " + age + "\n" + 				"Weight:  " + weight + "\n";		return str;	}} // end of Pet class.