package ch08;// FileName:  QuadraticDriver.java// THIS DRIVER IS USED TO DO BASIC TESTING OF THE Quadratic Class./*	The following Quadratic Polynomials can be used to test your Quadratic class:	x^2 - 2x - 8     factors into (x + 2)(x - 4)  giving the roots:  -2 and 4	x^2 - 10x + 25   factors into (x - 5)(x - 5)  giving the roots:   5 and 5	x^2 + 4x + 4     factors into (x + 2)(x + 2)  giving the roots:   2 and 2	x^2 - 6x + 9     factors into (x - 3)(x - 3)  giving the roots:   3 and 3	x^2 + 4x + 3     factors into (x + 3)(x + 1)  giving the roots:  -3 and -1	x^2 + 2x + 4   doesn't factor (no real roots) therefore giving the roots:  NAN and NAN	x^2 + 4x + 5   doesn't factor (no real roots) therefore giving the roots:  NAN and NAN*/import java.util.Scanner;public class QuadraticDriver{	public static void main (String[] args)	{		Scanner reader = new Scanner (System.in);				System.out.println("This program calculates the roots of a Quadratic Polynomial.\n");						String answer = "y";		while (answer.compareTo("y") == 0)		{			System.out.println("Enter the coefficients for the Quadratic Polynomial.\n");					System.out.print("Enter an integer for the coefficient a: ");					int a = reader.nextInt();			System.out.print("Enter an integer for the coefficient b: ");					int b = reader.nextInt();						System.out.print("Enter an integer for the coefficient c: ");					int c = reader.nextInt();			// Call the initializing constructor of the Quadratic class and pass			// it the data entered from the keyboard above.			Quadratic q1 = new Quadratic(a, b, c);									// Call the toString() method of the Quadratic class implicitly to			// print the state of the quadratic.			System.out.println(q1.toString());						// Prompt the user to "Enter an integer for x so the quadratic can be evaluated: "			System.out.print("Enter an integer for \"x\" so the quadratic can be evaluated: ");						// Read the value from the keyboard and store it in the variable x			int x = reader.nextInt();						// Call the evaluateQuadratic for the Quadratic object and store the value			// returned in the integer variable named y.			int y = q1.evaluateQuadratic(x);						// Print the value of y and print its value along with all of the other crucial			// information related to the Quadratic as seen on the sample output web page.			System.out.println("The Quadratic " + a + "x^2+" + b + "x+" + c + " evaluates to " + y + " when the value of x is "  + x);			System.out.println();			reader.nextLine();			System.out.print("Type \"y\" to contine, otherwise type any other key to Quit: ");			answer = reader.nextLine();			System.out.println();		} // end while loop				System.out.println();		System.out.println();				// This final code is for testing your default constructor of the		// Quadratic class once the loop ends.  Do Not Erase.		Quadratic defaultQ = new Quadratic ();				System.out.println(defaultQ);				System.out.println();		System.out.println("Program Terminated.");	} // end of main method} // end of QuadraticDriver.java